mapper.insert:
insert into ticket values
(ticket_seq.nextval
,#{localId}
,#{categoryId}
,#{memberNo}
,#{expireDate}
,#{ticketTitle}
,#{ticketContent}
,#{importantContent}
,#{businessTime}
,#{businessAddr}
,#{requiredTime}
,0,0,#{startDate})

	select max(ticket_no) from ticket

insert into payment values
(payment_seq.nextval
,#{payPrice}
,#{pointUse}
,#{payCoupon}
,#{pointAdd}
,sysdate
,#{payCouponCode},0)

	select max(pay_no) from payment
